content.query:
let logins = customEvents
| where timestamp {TimeRange}
| where name == "Auth_Login_Success"
| summarize Logins = count();
let eventViews = pageViews
| where timestamp {TimeRange}
| where name contains "event"
| summarize EventViews = dcount(session_Id);
let registrations = customEvents
| where timestamp {TimeRange}
| where name == "Attendance_Register_Submit"
| summarize Registrations = count();
let eventsCreated = customEvents
| where timestamp {TimeRange}
| where name == "Event_Create_Submit"
| summarize EventsCreated = count();
logins
| extend dummy = 1
| join kind=fullouter (eventViews | extend dummy = 1) on dummy
| join kind=fullouter (registrations | extend dummy = 1) on dummy
| join kind=fullouter (eventsCreated | extend dummy = 1) on dummy
| project Step = pack_array("1. Logins", "2. Event Views", "3. Registrations", "4. Events Created"), 
         Count = pack_array(Logins, EventViews, Registrations, EventsCreated)
| mv-expand Step to typeof(string), Count to typeof(long)
